import React,{useState,useEffect} from 'react';
import  '../styles/App.css';
import {BrowserRouter as Router,Route } from 'react-router-dom' ;
import Cart from './Cart'
import {Navbar,List} from '../components'
import { list } from '../data';


const SideMenu = ({loadCategory,category}) =>{
    const links=["Fruits","Légumes","Produits frais","Epicerie","Boissons"]

    return (
<div className="col-sm-2 sidebar">
        <ul>
            {links.map((link,index) =>{
                return(<li key={index} className={category == index && 'active'}  onClick={() => loadCategory(index)}>{link}</li>)
            })}
        </ul>
    </div>
    )
}


const App = () => {
    const [category,setCategory] = useState(0)
    const [isFiltering,setFiltering] = useState(false)
    const [filtered,setFiltered] = useState(false)
  const [count,setCount] = useState(1);
    const loadCategory = i => {
        setCategory(i)
    }
    const filterResult = (input) => {
        let fullList = list.flat()
        let results = fullList.filter(item =>{
            const name = item.name.toLowerCase()
            const term = input.toLowerCase()
            return name.indexOf(term) > -1
        })
        setFiltered(results)

    }
    useEffect(() =>{
console.log(isFiltering)
    })
    return ( 
        <>
        <Router>
        <Navbar filter={filterResult} setFiltering = {setFiltering} count={count}/>
        <div className="container">
<div className="row">
    <SideMenu loadCategory={loadCategory} category={category}/>
    <div className="col-sm">
    <div className="row">
       <List data={isFiltering ? filtered :list[category]} category={category} addToCart ={setCount} count={count}/>
    </div>
    </div>

</div>



        </div>
        <Route path="/" component={Cart}/>
        <Route path="/cart" component={Cart}/>
        </Router>
        </>
     );
}

export default App;
