import React, { Fragment, useContext, useEffect } from "react";
import { useSelector } from 'react-redux'
import { Link } from "react-router-dom";
import "../../styles/Confirm.css";
import { UserProfileContext } from '../../lib/UserProfileContext'

// import { useSelector } from "react-redux";
// import { UserProfileContext } from "../lib/UserContext";
// import { Redirect } from "react-router-dom";
// import FirebaseAPI from "../config/Firebase";
//import "../styles/App.css";
//import "./Confirm.css";



export const Confirm = () => {
    const userProfile = useContext(UserProfileContext);
    const items = useSelector(state => state.items)
    return (
        <Fragment>
            <div class="jumbotron text-center">
                <h1 class="display-3">Thank You!</h1>
                <hr />

                <p class="lead">
                    <strong>Your order is on its way to your home</strong>
                    <br /> <br />
                    <Link class="btn btn-primary btn-sm" to="/">
                        Continue to homepage
          </Link>
                </p>
                <br />
                {userProfile.address &&
                    <ul className="">
                        <li>{(userProfile.lastName).toLocaleUpperCase()},{userProfile.firstName})</li>
                        <li>{userProfile.address}</li>
                        <li>{userProfile.zipCode}, {userProfile.city}</li>

                    </ul>}

            </div>
        </Fragment>
    );
}
